import React, { Component } from "react";
import { Button, View, Text, AsyncStorage, StyleSheet, TouchableOpacity } from "react-native";
import { styles, buttons } from './styles';

export default class Home extends Component {
  constructor(props) {
    super(props);
    this.handleLogin = this.handleLogin.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
    this.state = {
      username: "",
      password: "",
      token: "",
      isLoggedIn: Boolean,
      en: false, //if true = english rendering
      url: "http://192.168.178.105:9000/api", // hier die IP des Laptops angeben, mit der der Server lÃ¤uft
    };
  }

  onChangeUsername = (text) => {
    this.setState({
      username: text,
    });
  };
  onChangePassword = (text) => {
    this.setState({
      password: text,
    });
  };

  handleLogin() {
    const url = this.state.url;
    fetch(url + "/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username: this.state.username,
        password: this.state.password,
      }),
    })
      .then((res) => res.json())
      .then((response) => {
        const token = response.token;
        //AsyncStorage.getItem('TASKS');
        this.setState({
          token: token,
        });
        AsyncStorage.setItem("token", this.state.token);
        if (token != null) {
          this.setState({
            isLoggedIn: true,
          });
          this.props.navigation.goBack("Home");
          console.log("Eingeloggt");
        } else {
          console.log("Nicht geklappt");
        }
      })

      .catch((err) => console.log(err));
  }

  handleLogout() {
    AsyncStorage.removeItem("token");
    this.setState({
      isLoggedIn: false,
    });
    this.props.navigation.reset({
      index: 0,
      routes: [{ name: "Home" }],
    });
    console.log("Ausgeloggt");
  }

  render() {
    if (this.state.isLoggedIn === true) {
      return (
        <View 
          // style={{ flex: 1, alignItems: "center", justifyContent: "center" }}
        >
          <TouchableOpacity style={buttons.primary}
            onPress={() => this.setState({ en: true })}
          >
            <Text>EN</Text>
          </TouchableOpacity>
          <TouchableOpacity style={buttons.primary}
            onPress={() => this.setState({ en: false })}
          >
            <Text>DE</Text>
          </TouchableOpacity>

          <TouchableOpacity style={buttons.primary}
            title="Fitnessinfo"
            color = "#ffa600"
            onPress={() =>
              this.props.navigation.navigate("FitnessinfoAdmin", {
                en: this.state.en,
                url: this.state.url,
              })
            }
          />
          <TouchableOpacity style={buttons.primary}
            title="Fitnessplan"
            color = "#ffa600"
            onPress={() =>
              this.props.navigation.navigate("FitnessplanAdmin", {
                en: this.state.en,
                url: this.state.url,
              })
            }
          />
          <TouchableOpacity style={buttons.primary}
            title="Logout"
            color = "#ffa600"
            onPress={() => {
              this.handleLogout();
            }}
          />
        </View>
        
      );
    } else {
      return (
          <View style={[{ 
          flex: 1,
          margin: 80,
          justifyContent: "center",
          }]}>
          <TouchableOpacity style={buttons.primary}
            onPress={() => this.setState({ en: true })}
          >
            <Text>EN</Text>
          </TouchableOpacity>
          
          <TouchableOpacity style={buttons.primary}
            onPress={() => this.setState({ en: false })}
          >
            <Text>DE</Text>
          </TouchableOpacity>

          <TouchableOpacity style={buttons.primary}
            onPress={() =>
              this.props.navigation.navigate("Fitnessinfo", {
                en: this.state.en,
                url: this.state.url,
              })
            }
            >
              <Text>Fitnessinfo</Text>
            </TouchableOpacity>
          
          <TouchableOpacity style={buttons.primary}
            onPress={() =>
              this.props.navigation.navigate("Fitnessplan", {
                en: this.state.en,
                url: this.state.url,
              })
            }
          >
            <Text>Fitnessplan</Text>
          </TouchableOpacity>
          {
            <TouchableOpacity style={buttons.primary}
              title="Login"
              color = "#ffa600"
              onPress={() =>
                this.props.navigation.navigate("Login", {
                  en: this.state.en,
                  handleLogin: this.handleLogin.bind(this),
                  handleLogout: this.handleLogout.bind(this),
                  onChangeUsername: this.onChangeUsername.bind(this),
                  onChangePassword: this.onChangePassword.bind(this),
                })
              }
              >
                <Text>Login</Text>
              </TouchableOpacity>
          }
        </View>
      );
    }
  }
}
